import { FTypo, FGrid, FTable, FHeader, FButton } from "ferrum-design-system";
import { useEffect } from "react";
import Datatable from "react-bs-datatable";
import { useDispatch, useSelector } from "react-redux";
import { crucibleApi } from "../client";
import { fetchWithdrawals } from "../redux/casper/casperActions";

import './layout.scss';

export const Withdrawals = () => {
    const { connect: { config, selectedAccount, isWalletConnected, signedAddresses, withdrawalItems } } = useSelector((state: any) => state.casper);
    const { walletAddress } = useSelector((state: any) => state.casper.walletConnector);

    const dispatch = useDispatch();

    const fetchEvmWithdrawalItems = async () => {
        const Api = new crucibleApi()
        await Api.signInToServer(`CSPR:${selectedAccount?.address}`)
        const userWithdrawals = await Api.gatewayApi({
            command: 'getUserNonEvmWithdrawItems', data: {
            userAddress: `CSPR:${selectedAccount?.address}`,
            network: '56',
            receiveAddress: walletAddress,
        }, params: [] });
        if (userWithdrawals.data){
            fetchWithdrawals(userWithdrawals.data);
        }
        console.log(userWithdrawals, 'userWithdrawals');
    }

    useEffect(() => {
       fetchEvmWithdrawalItems()
    }, [selectedAccount, walletAddress]);

    const tableHeads: any[] = [
        { width: 200, prop: "sourceNetwork", title: "Source Network" },
        { prop: "targetNetwork", title: "Target Network" },
        { prop: "amount", title: "Amount" },
        { prop: "action", title: "Action" }
    ];

    const body = withdrawalItems.map((item) => { 
        return {
          sustainableCrucibleFarms: (
            <FItem data-label="Sustainable Crucible Farms" className={"col-crucible-farming"} display={"flex"} alignY="center">
              <span className="network-icon-wrap f-mr-1">
                <IconNetworkCFrm />
                <IconNetworkBsc />
              </span>
              {stepFlow.sustainableCrucibleFarms}
            </FItem>
          ),
          totalDeposited: <FTypo className={"col-amount"}>{stepFlow.totalDeposited}</FTypo>,
          yourDeposit: <FTypo className={"col-amount"}>{stepFlow.yourDeposit}</FTypo>,
          yourRewards: <FTypo className={"col-amount"}>{stepFlow.yourRewards}</FTypo>,
          apr: ( <FTypo className={"col-amount"} size={24} color="#DAB46E" weight={500}> {stepFlow.apr} </FTypo> ),
          action: (
            <div className="col-action">
              <FButton title={"Withdraw"} onClick={()=> {}} />
            </div>
          ),
        }; 
    }); 

    return (
        <FGrid alignX={"center"} className="f-mb-1 withdrawals_container">
            <FTypo size={18} align={"center"} className={"f-mb-14 f-mt--7"}>
                WITHDRAWALS
            </FTypo>
            <FTable>
                <Datatable tableBody={[]} tableHeaders={tableHeads} rowsPerPage={10} />
            </FTable>
        </FGrid>
    )
}
