import React from "react";
import { FGrid, FCard, FGridItem, FContainer, FInputText, FButton, FDatepicker } from "ferrum-design-system";
import moment from "moment";
import { useForm } from "react-hook-form";
import { Toaster } from "react-hot-toast";
import { useDispatch, useSelector } from "react-redux";
import { CLPublicKey, CasperClient, CasperServiceByJsonRPC, CLValueBuilder, decodeBase16, DeployUtil, RuntimeArgs, Signer } from "casper-js-sdk";
// import * as stakingActions from "../redux/reducers/staking/stakingAction";

export const InputForm = () => {
  const dispatch: any = useDispatch();
  const connection = useSelector((state: any) => state.casper.connect)
  const { connect: { config, selectedAccount, isWalletConnected, signedAddresses } } = useSelector((state: any) => state.casper);

  console.log(connection, 'connectionconnection')
  // const { actionLoading } = useSelector((state: any) => state.staking);

  const initialValues = {
    tokenAddress: "",
    stakingCap: "",
    stakingStarts: "",
    stakingEnds: "",
    withdrawStarts: "",
    withdrawEnds: "",
  };

  const {
    // reset,
    register,
    control,
    handleSubmit,
    formState: {
      errors,
      //  isSubmitting
    },
    // watch,
  } = useForm({ defaultValues: initialValues });

  const onSubmit = async (values: any) => {
    console.log(values, 'valuesss');
    try {
      // console.log(selectedAccount?.address, Number(amount));
      const publicKeyHex = selectedAccount?.address;
      const senderPublicKey = CLPublicKey.fromHex(publicKeyHex);

      const deployParams = new DeployUtil.DeployParams(
        senderPublicKey,
        'casper-test'
      );

      const args = RuntimeArgs.fromMap({
        "amount": CLValueBuilder.u256(amount),
      });

      const session = DeployUtil.ExecutableDeployItem.newStoredContractByHash(
        decodeBase16(stakingId),
        'stake',
        args
      );

      const payment = DeployUtil.standardPayment(10000000000000);

      const deploy = DeployUtil.makeDeploy(deployParams, session, payment);

      const deployJson: any = DeployUtil.deployToJson(deploy);

      Signer.sign(deployJson, publicKeyHex).then(async (signedDeployJson) => {
        const signedDeploy = DeployUtil.deployFromJson(signedDeployJson);
        console.log(signedDeploy)
        if (signedDeploy.ok) {
          const res = await casperClient.putDeploy(signedDeploy.val);
          console.log(res, 'resres');
          setProcessMsg(res)
          setLoading(false)
          setShowConfirmation(true)
        }

      });
        // navigate.push(`/${config._id}`);
        //toast.success(`${amount} tokens are staked successfully`);
    } catch (e) {
      console.log("ERROR : ", e);
      toast.error("An error occured please see console for details");
      navigate.push(`/${config._id}`);
    } finally {
      //setLoading(false)
    }
    // console.log(values.stakingStarts); // pick by date time picker
    // console.log(moment.utc(values.stakingStarts).format("YYYY-MM-DD HH:mm:ss Z")); //Local to UTC
    // var stillUtc = moment.utc(moment.utc(values.stakingStarts).format("YYYY-MM-DD HH:mm:ss Z")).toDate();
    // console.log(moment(stillUtc).local().format("YYYY-MM-DD HH:mm:ss")); // UTC to Local

    // dispatch(
    //   stakingActions.createStaking({
    //     ...values,
    //     stakingStarts: moment(values.stakingStarts).format("yyyy-MM-DD HH:mm:ss.SSS"),
    //     stakingEnds: moment(values.stakingEnds).format("yyyy-MM-DD HH:mm:ss.SSS"),
    //     withdrawStarts: moment(values.withdrawStarts).format("yyyy-MM-DD HH:mm:ss.SSS"),
    //     withdrawEnds: moment(values.withdrawEnds).format("yyyy-MM-DD HH:mm:ss.SSS"),
    //   })
    // );
  };

  return (
    <>
      <Toaster />
      <FContainer width={1200}>
        <FCard variant={"primary"} className="f-mt-2 f-mb-2">
          <form autoComplete="false" onSubmit={handleSubmit(onSubmit)}>
            <FGrid className={"f-mt-1"}>
              <FGridItem size={[6, 6, 6]}>
                <FInputText
                  label="Token Address"
                  name="tokenAddress"
                  type="text"
                  className={"w-100"}
                  placeholder="0"
                  register={register}
                  validations={{
                    required: {
                      value: true,
                      message: "Token Address is required",
                    },
                  }}
                  error={errors["tokenAddress"]?.message ? errors["tokenAddress"]?.message : ""}
                />
              </FGridItem>
              <FGridItem size={[6, 6, 6]}>
                <FInputText
                  label="stakingCap"
                  name="stakingCap"
                  type="text"
                  placeholder="0"
                  register={register}
                  validations={{
                    required: {
                      value: true,
                      message: "Staking Cap is required",
                    },
                  }}
                  error={errors["stakingCap"]?.message ? errors["stakingCap"]?.message : ""}
                />
              </FGridItem>
              <FGridItem size={[6, 6, 6]}>
                <FDatepicker
                  label="Staking Starts"
                  name="stakingStarts"
                  className={"f-mt-1"}
                  placeholderText="MM-DD-YYYY"
                  register={register}
                  showTimeSelect={true}
                  control={control}
                  validations={{
                    required: {
                      value: true,
                      message: "Staking Starts is required",
                    },
                  }}
                  error={errors["stakingStarts"]?.message ? errors["stakingStarts"]?.message : ""}
                />
              </FGridItem>
              <FGridItem size={[6, 6, 6]}>
                <FDatepicker
                  label="Staking Ends"
                  name="stakingEnds"
                  className={"f-mt-1"}
                  placeholderText="MM-DD-YYYY"
                  register={register}
                  showTimeSelect={true}
                  control={control}
                  validations={{
                    required: {
                      value: true,
                      message: "Staking Ends is required",
                    },
                  }}
                  error={errors["stakingEnds"]?.message ? errors["stakingEnds"]?.message : ""}
                />
              </FGridItem>
              <FGridItem size={[6, 6, 6]}>
                <FDatepicker
                  label="Withdraw Starts"
                  name="withdrawStarts"
                  className={"f-mt-1"}
                  placeholderText="MM-DD-YYYY"
                  register={register}
                  showTimeSelect={true}
                  control={control}
                  validations={{
                    required: {
                      value: true,
                      message: "Withdraw Starts is required",
                    },
                  }}
                  error={errors["withdrawStarts"]?.message ? errors["withdrawStarts"]?.message : ""}
                />
              </FGridItem>
              <FGridItem size={[6, 6, 6]}>
                <FDatepicker
                  label="Withdraw Ends"
                  name="withdrawEnds"
                  className={"f-mt-1"}
                  placeholderText="MM-DD-YYYY"
                  register={register}
                  showTimeSelect={true}
                  control={control}
                  validations={{
                    required: {
                      value: true,
                      message: "Withdraw Ends is required",
                    },
                  }}
                  error={errors["withdrawEnds"]?.message ? errors["withdrawEnds"]?.message : ""}
                />
              </FGridItem>
              <FGridItem alignX={"end"}>
                <FButton
                  type="submit"
                  disabled={!connection.connectedAccounts.length}
                  title={!!connection.connectedAccounts.length ? "Create Staking" : 'Connect wallet to create' }
                  className={"f-mt-1 f-mb-2"}
                  // postfix={isSubmitting && <ClipLoader color="#fff" size={20} />}
                ></FButton>
              </FGridItem>
            </FGrid>
          </form>
        </FCard>
      </FContainer>
    </>
  );
};
