import { useState } from "react";
import { FCard, FGrid, FGridItem, FInputText, FItem, FTypo } from "ferrum-design-system";
import { useSelector } from "react-redux";
import { getStakingInfo } from "../utils/DateUtil";

export const CasperSwap = () => {
  const { isWalletConnected, config, tokenInfo } =
  useSelector((state: any) => state.casper.connect);

  // const [stakingCap, setStakingCap] = useState<any>(undefined);
  const [stakeSoFar, setStakeSoFar] = useState<any>(undefined);
  const [youStakedBalance, setYourStakedBalance] = useState<any>(undefined);

  const stakingInfo = getStakingInfo(
    config?.stakingEnds,
    config?.stakingStarts,
    config?.withdrawStarts,
    config?.withdrawEnds
  );

  console.log(config, stakingInfo, tokenInfo, 'configconfigconfig')
  // console.log(stakingCap, stakeSoFar, youStakedBalance);

  return (
    <FCard className={"card-staking f-mb-2"}>
      <FGrid>
        <FGridItem alignX={"center"} size={[6, 6, 12]} className="f-mb-1">
          <FItem align={"center"}>
            <FTypo size={18} align={"center"} className={"f-mb--5 f-mt--7"}>
              MATURITY REWARD
            </FTypo>
            <FInputText
              className={"f-mt-2"}
              label={"AMOUNT TO STAKE"}
              placeholder={"0"}
              value={''}
              onChange={(e: any) => {
                e.preventDefault();
                const re = /^-?\d*\.?\d*$/;
                if (e.target.value === "" || re.test(e.target.value)) {
                  //setAmount(e.target.value);
                }
              }}
              postfix={
                <FTypo className={"f-pr-1"} color="#dab46e">
                  TOKEN
                </FTypo>
              }
            />
            <FInputText
              className={"f-mt-2"}
              label={"AMOUNT TO STAKE"}
              placeholder={"0"}
              value={''}
              onChange={(e: any) => {
                e.preventDefault();
                const re = /^-?\d*\.?\d*$/;
                if (e.target.value === "" || re.test(e.target.value)) {
                  //setAmount(e.target.value);
                }
              }}
              postfix={
                <FTypo className={"f-pr-1"} color="#dab46e">
                  TOKEN
                </FTypo>
              }
            />
          </FItem>
        </FGridItem>
        <FGridItem alignX={"center"} size={[6, 6, 6]} className="f-mb-1">
          <FItem align={"center"}>
            <FInputText
              className={"f-mt-2"}
              label={"AMOUNT TO STAKE"}
              placeholder={"0"}
              value={''}
              onChange={(e: any) => {
                e.preventDefault();
                const re = /^-?\d*\.?\d*$/;
                if (e.target.value === "" || re.test(e.target.value)) {
                  //setAmount(e.target.value);
                }
              }}
              postfix={
                <FTypo className={"f-pr-1"} color="#dab46e">
                  TOKEN
                </FTypo>
              }
            />
          </FItem>
        </FGridItem>
        <FGridItem alignX={"center"} size={[12, 12, 12]} className="f-mb-1">
          <FItem align={"center"}>
            <FInputText
              className={"f-mt-2"}
              label={"AMOUNT TO STAKE"}
              placeholder={"0"}
              value={''}
              onChange={(e: any) => {
                e.preventDefault();
                const re = /^-?\d*\.?\d*$/;
                if (e.target.value === "" || re.test(e.target.value)) {
                  //setAmount(e.target.value);
                }
              }}
              postfix={
                <FTypo className={"f-pr-1"} color="#dab46e">
                  TOKEN
                </FTypo>
              }
            />
          </FItem>
        </FGridItem>
