import React from "react";
import { Route, Switch } from "react-router";
import { LandingPage } from "./pages/Landing/LandingPage";
import { FLayout, FMain, FLoader, FContainer } from "ferrum-design-system";
import Header from "./header/header";
import Loader from "../../../assets/img/loader-m.png";

const Wrapper = () => {
  return (
    <>
      <Header />
      <FLoader className="dialogbox-load" loading={true} width={100} loaderImage={Loader} />
      <FMain>
        <LandingPage />
      </FMain>
    </>
  )
}

const BaseRoutes = () => {
  return (
    <Switch>
      <Route path="/:stakingId" component={
        Wrapper
      }></Route>
    </Switch>
  );
};
export default BaseRoutes;
