import React, { useState } from "react";
import { ReactComponent as IconClose } from "../assets/images/f-icons/f-icon-close.svg";

interface FResponseBarProps {
  className?: string;
  variant?: string;
  title?: string;
  onClose?: any;
  show?: boolean;
}

export const FResponseBar = ({
  className = "",
  variant = "",
  title = "",
  show = false,
  onClose = () => {},
}: FResponseBarProps) => {
  return (
    <React.Fragment>
      {show && (
        <div
          className={`f-response-bar f-variant f-variant-${variant} ${className}`}
        >
          <span>{title}</span>{" "}
          <IconClose className="icon-close" onClick={() => onClose()} />
        </div>
      )}
    </React.Fragment>
  );
};
